mapper.select:
SELECT * FROM problem
WHERE status = 1 AND id = #{id}
LIMIT 1;

mapper.update:
UPDATE problem
SET status = 0
WHERE id = #{id}

mapper.select:
SELECT *
FROM user_problem_relationship

    status = 1
    AND user_id = #{userId}
    AND exam_id = #{examId}
    AND problem_id = #{ProblemId}

LIMIT 1;
